package com.java.banksystem.server.entity;import com.fasterxml.jackson.annotation.JsonIgnore;import jakarta.persistence.*;import lombok.Data;import java.math.BigDecimal;import java.util.List;@Data@Entity@Table(name = "user_accounts")public class Account {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(name = "account_number", nullable = false, unique = true)    private String accountNumber;    @Column(nullable = false)    private BigDecimal balance;    @OneToOne    @JoinColumn(name = "user_id", referencedColumnName = "id")    private User user;    @OneToMany(mappedBy = "senderAccount", cascade = CascadeType.ALL)    @JsonIgnore    private List<Transaction> transaction;    @OneToMany(mappedBy = "recipientAccount", cascade = CascadeType.ALL)    @JsonIgnore    private List<Transaction> incoming;}