package com.java.banksystem.server.entity;import com.fasterxml.jackson.annotation.JsonIgnoreProperties;import jakarta.persistence.*;import lombok.Data;import org.hibernate.annotations.CreationTimestamp;import org.hibernate.annotations.OnDelete;import org.hibernate.annotations.OnDeleteAction;import org.springframework.data.annotation.CreatedDate;import java.math.BigDecimal;import java.time.LocalDateTime;@Data@Entity@Table(name = "transaction")public class Transaction {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @ManyToOne    @JsonIgnoreProperties("transactions")    @JoinColumn(name = "sender_account_id", nullable = false)    private Account senderAccount;    @ManyToOne    @JsonIgnoreProperties("transactions")    @JoinColumn(name = "recipient_account_id", nullable = false)    private Account recipientAccount;    @Column(nullable = false)    private BigDecimal amount;    @CreationTimestamp    private LocalDateTime createdAt;}