package com.java.banksystem.server.entity;import com.fasterxml.jackson.annotation.JsonIgnore;import jakarta.persistence.*;import lombok.Data;import org.hibernate.annotations.OnDelete;import org.hibernate.annotations.OnDeleteAction;import java.util.ArrayList;import java.util.List;@Data@Entity@Table(name = "users")public class User {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    private String name;    private String password;    @OneToOne(mappedBy = "user", cascade = CascadeType.ALL)    @JsonIgnore    private Account account;}