package com.java.banksystem.server.mapper;import com.java.banksystem.server.entity.Account;import com.java.banksystem.web.dto.AccountDto;import org.mapstruct.Mapper;import org.mapstruct.Mapping;import org.mapstruct.factory.Mappers;import java.util.List;@Mapperpublic interface AccountMapper {    AccountMapper INSTANCE = Mappers.getMapper(AccountMapper.class);    @Mapping(source = "id", target = "id")    @Mapping(source = "accountNumber", target = "accountNumber")    @Mapping(source = "balance", target = "balance")    @Mapping(source = "user", target = "user")    AccountDto accountToAccountDto(Account account);    List<AccountDto> toAccountListDto(List<Account> accountList);    @Mapping(source = "id", target = "id")    @Mapping(source = "accountNumber", target = "accountNumber")    @Mapping(source = "balance", target = "balance")    @Mapping(source = "user", target = "user")    Account accountDtoToAccount(AccountDto accountDto);}