package com.java.banksystem.server.mapper;import com.java.banksystem.server.entity.Transaction;import com.java.banksystem.web.dto.TransactionDto;import org.mapstruct.Mapper;import org.mapstruct.Mapping;import org.mapstruct.factory.Mappers;import java.util.List;@Mapperpublic interface TransactionMapper {    TransactionMapper INSTANCE  = Mappers.getMapper(TransactionMapper.class);    @Mapping(source = "id", target = "id")    @Mapping(source = "senderAccount", target = "senderAccount")    @Mapping(source = "recipientAccount", target = "recipientAccount")    @Mapping(source = "amount", target = "amount")    @Mapping(source = "createdAt", target = "createdAt")    TransactionDto transactionToTransactionDTO(Transaction transaction);    @Mapping(source = "id", target = "id")    @Mapping(source = "senderAccount", target = "senderAccount")    @Mapping(source = "recipientAccount", target = "recipientAccount")    @Mapping(source = "amount", target = "amount")    @Mapping(source = "createdAt", target = "createdAt")    Transaction transactionDTOToTransaction(TransactionDto transactionDto);    List<TransactionDto> toTransactionDtoList(List<Transaction> transactions);}