package com.java.banksystem.server.mapper;import com.java.banksystem.server.entity.User;import com.java.banksystem.web.dto.UserDto;import org.mapstruct.Mapper;import org.mapstruct.Mapping;import org.mapstruct.factory.Mappers;import java.util.List;@Mapperpublic interface UserMapper {    UserMapper INSTANCE = Mappers.getMapper(UserMapper.class);    @Mapping(source = "id", target = "id")    @Mapping(source = "name", target = "name")    @Mapping(source = "password", target = "password")    UserDto userToUserDto(User user);    List<UserDto> userListToUserDto(List<User> users);    @Mapping(source = "id", target = "id")    @Mapping(source = "name", target = "name")    @Mapping(source = "password", target = "password")    User userDtoToUser(UserDto userDto);}