package com.java.banksystem.web.controller;import com.java.banksystem.server.service.AccountService;import com.java.banksystem.server.service.UserService;import com.java.banksystem.web.dto.AccountDto;import com.java.banksystem.web.dto.UserDto;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.math.BigDecimal;import java.util.List;@RestController@RequestMapping("api/v1/account")public class AccountController {    private final AccountService accountService;    private final UserService userService;    public AccountController(AccountService accountService, UserService userService) {        this.accountService = accountService;        this.userService = userService;    }    @PostMapping("{user_id}")    public ResponseEntity<AccountDto> createAccount(@PathVariable Long user_id,                                                    @RequestBody AccountDto accountDto) {        AccountDto account = accountService.createAccount(user_id, accountDto);        return new ResponseEntity<>(account, HttpStatus.CREATED);    }    @GetMapping    public ResponseEntity<List<AccountDto>> getAllAccounts() {        return new ResponseEntity<>(accountService.getAllAccounts(), HttpStatus.OK);    }    @GetMapping("{id}")    public ResponseEntity<AccountDto> getAccountById(@PathVariable Long id) {        return new ResponseEntity<>(accountService.getAccountById(id), HttpStatus.OK);    }    @DeleteMapping("{id}")    public ResponseEntity<Void> deleteAccount(@PathVariable Long id) {        accountService.deleteAccount(id);        return new ResponseEntity<>(HttpStatus.NO_CONTENT);    }}