package com.java.banksystem.web.controller;import com.java.banksystem.server.exception.NotEnoughBalanceException;import com.java.banksystem.server.service.TransactionService;import com.java.banksystem.web.dto.TransactionDto;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.math.BigDecimal;import java.util.List;@RestController@RequestMapping("api/v1/transaction")public class TransactionController {    private final TransactionService transactionService;    public TransactionController(TransactionService transactionService) {        this.transactionService = transactionService;    }    @GetMapping    public ResponseEntity<List<TransactionDto>> getAllTransactions() {        return new ResponseEntity<>(transactionService.getAllTransactions(), HttpStatus.OK);    }    @PostMapping    public ResponseEntity<?> createTransaction(@RequestBody TransactionDto transactionDto) {        try {            TransactionDto transaction = transactionService.createTransaction(transactionDto);            return new ResponseEntity<>(transaction, HttpStatus.CREATED);        } catch (NotEnoughBalanceException ex) {            return ResponseEntity.status(HttpStatus.BAD_REQUEST).body(ex.getMessage());        }    }}